function StartMe() {
	    //  header element
    const header = document.createElement('header');
    header.classList.add('header');

    //  menu div
    const menuDiv = document.createElement('div');
    menuDiv.classList.add('menu');
    menuDiv.innerHTML = '<img id="menu" src="icons/menu.png" alt="menu">';
    header.appendChild(menuDiv);

    //  logo div
    const logoDiv = document.createElement('div');
    logoDiv.classList.add('logo');
    logoDiv.innerHTML = '<img id="logo" src="images/gearbox.png" alt="logo">';
    header.appendChild(logoDiv);

    //  user-info div
    const userInfoDiv = document.createElement('div');
    userInfoDiv.classList.add('user-info');
    header.appendChild(userInfoDiv);

    //  notification icon
    const notificationIcon = document.createElement('img');
    notificationIcon.id = 'notificationIcon';
    notificationIcon.src = 'icons/notifications.png';
    notificationIcon.alt = 'Notification Icon';
    header.appendChild(notificationIcon);

    document.body.appendChild(header);

    //  body elements
    const bodyDiv = document.createElement('div');
    bodyDiv.classList.add('body');

    //  hr element
    const hr = document.createElement('hr');
    hr.id = 'hr';
    bodyDiv.appendChild(hr);

    //  navigation element
    const navigationNav = document.createElement('nav');
    navigationNav.classList.add('navigation');
    navigationNav.innerHTML = `<ul>
        <li><a href="#"><img id="icons" src="icons/logout.png" alt="Logout Icon"> Log Out</a></li>
		<li><a href="#"><img id="icons" src="icons/help.png" alt="Logout Icon">Help</a></li>
        <li><a href="#"><img id="icons" src="icons/settings.png" alt="Logout Icon">System Administration</a></li>
        <li><a href="#"><img id="icons" src="icons/calender.png" alt="Logout Icon">Calendar Events </a></li>
        <li><a href="#"><img id="icons" src="icons/access.png" alt="Logout Icon">Recent Company Messages</a></li>
        <li><a href="#"><img id="icons" src="icons/mail.png" alt="Logout Icon">Mailbox</a></li>
        <li><a href="#"><img id="icons" src="icons/files.png" alt="Logout Icon">Dynamic Reports</a></li>
        <li><a href="#"><img id="icons" src="icons/calender.png" alt="Logout Icon">Calendar</a></li>
        <hr>
        <li><a href="#"><img id="icons" src="icons/network.png" alt="Logout Icon">View Network</a></li>
        <hr>
        <li><a href="#"><img id="icons" src="icons/dashboard.png" alt="Logout Icon">Dashboard</a></li>
        <li><a href="#"><img id="icons" src="icons/documents.png" alt="Logout Icon">Our Documents</a></li>
        <li><a href="#"><img id="icons" src="icons/third.png" alt="Logout Icon">3rd Party Management</a></li>
        <li><a href="#"><img id="icons" src="icons/application.png" alt="Logout Icon">Form Applications</a></li>
    </ul>`;
    bodyDiv.appendChild(navigationNav);

    //  card element
    const cardDiv = document.createElement('div');
    cardDiv.id = 'card';
    cardDiv.innerHTML = `<p>Good afternoon, John!</p>
    <br>
    <br>
    <div>
        What would you like to do today?
        <select>
            <option value=""></option>
            <option value="viewCalendar">View Calendar</option>
            <!-- Rest of the options -->
        </select>
    </div>`;
    bodyDiv.appendChild(cardDiv);

    document.body.appendChild(bodyDiv);

    //  main element
    const main = document.createElement('main');
    main.classList.add('main');

    // inbox section
    const inboxSection = document.createElement('section');
    inboxSection.id = 'inbox';
    inboxSection.classList.add('content-section');
    inboxSection.innerHTML = '<h2>Inbox</h2>';
    main.appendChild(inboxSection);

    //  calendar section
    const calendarSection = document.createElement('section');
    calendarSection.id = 'calendar';
    calendarSection.classList.add('content-section');
    calendarSection.innerHTML = '<h2>Calendar</h2>';
    main.appendChild(calendarSection);

    document.body.appendChild(main);
}
